const showResize = function (resizeObject) {
  //console.log(resizeObject)
}

storiesOf('Portal')
  .addDecorator(withKnobs)
  .add(
    'Sticky',
    () => {

      const scrollStyle = {
        width: '70vw',
        height: '70vh',
        border: '1px dotted #ccc',
        position: 'relative',
        overflow: 'scroll'
      }

      return (
        <Sanity part="part:@sanity/components/portal/sticky" propTables={[StickyPortal]}>
          <div
            style={scrollStyle}
            id="normalStickyScrollId"
          >
            {SMALLTEXT}
            {SMALLTEXT}
            {SMALLTEXT}
            <span style={{border: '1px solid red'}}>ROOT
              <StickyPortal
                onlyBottomSpace={boolean('onlyBottomSpace (prop)', true)}
                isOpen={boolean('isOpen (prop)', false)}
                onClose={action('onClose')}
                stickToTop={boolean('stickToTop (prop)', false)}
                scrollContainer={document.getElementById('normalStickyScrollId')}
                onResize={showResize}
              >
                <div
                  style={{
                    display: 'block',
                    border: '1px solid magenta'
                  }}
                >
                Content
                </div>
              </StickyPortal>
            </span>
            {TEXT}
          </div>
        </Sanity>
      )
    }
  )

